function getSumOfRepetitiveNumbers(arr,shift){
	var sum=0;
	for (i = 0, len = arr.length; i < len; i++) { 
	    	var j =  (i+shift) % len ;
		sum += arr[i]==arr[j] ? arr[i] : 0 ;
	}
	return sum;
}
function verify(expected,  converter, input){
	var actual = converter(input);
	var message = "Input: "+input+". Expected: "+expected+", actual: "+actual;
	if (expected!==actual){
		console.error(message);
	}else{
		console.info(message);
	}
}
function convertToNumbers( str){
	return str.split("").map(function(item){ return parseInt(item);})
}
function calculate1(inp){
	return getSumOfRepetitiveNumbers( convertToNumbers( inp ),1 );
}
function calculate2(inp){
	var converted = convertToNumbers( inp ); 
	var shift = Math.floor( converted.length / 2);
	//var shift = converted.length div 2;
	return getSumOfRepetitiveNumbers( converted, shift);
}
function test() {
	verify(3,calculate1, "1122"      );
	verify(4,calculate1, "1111"      );
	verify(0,calculate1, "1234"      );
	verify(9,calculate1, "91212129"  );
	console.log("test of 1 part is finished");

	verify(6,calculate2,"1212"      );
	verify(0,calculate2,"1221"     );
	verify(4,calculate2,"123425"      );
	verify(12,calculate2,"123123"   );
	verify(4,calculate2,"12131415"  );
	verify(2,calculate2,"11"  );
	verify(2,calculate2,"11"  );
	verify(0,calculate2,"00"  );
	console.log("test of 2 part is finished");
}

function resolveAndOutput(){
	output1 = calculate1(input);
	console.log(output1);	
	output2 = calculate2(input);
	console.log(output2);	
}

function realTest(){
	verify(1144,calculate1,input);
	verify(1194,calculate2,input);
}
